Actions.Action:
(block Nil
	(objEnumItems gSource "*" thisItem
		(block Nil
			(objRemoveItem gSource thisItem)
			(objAddItem gPlayerShip thisItem)
			)
		)
	(scrExitScreen gScreen 'forceUndock)
	)

(block Nil
	(objEnumItems gSource "*U" thisItem
		(block Nil
			(objRemoveItem gSource thisItem)
			(objAddItem gPlayerShip thisItem)
			)
		)
	(scrExitScreen gScreen 'forceUndock)
	)
