Events.OrderFreighter:
(block ( (freighterObj (objGetObjByID (msnGetData gSource 'freighterID))) ) (shpCancelOrders freighterObj) ; Ascending objects removes event handler, so reset: (objSetEventHandler freighterObj &evKorolovFreighterBehavior;) ; If we are in target system then dock at the target station, otherwise onto the next gate (if (= (sysGetNode) (msnGetData gSource 'navNodeID)) (shpOrder freighterObj 'dock (objGetObjByID (msnGetData gSource 'navID))) (shpOrder freighterObj 'gate (sysGetStargateLeadingToNode (msnGetData gSource 'navNodeID))) )
; If we are in destination system then add some pirates (if (and (= (sysGetNode) (msnGetData gSource 'navNodeID)) (msnGetData gSource 'triggerAttack) ) (block Nil ; Order the pirates (chrRaidTransport freighterObj (sysGetStargateLeadingToNode (msnGetData gSource 'srcNodeID)) (objGetObjByID (msnGetData gSource 'destID)) (msnGetData gSource 'cargoValue) ) ; Only do this once (msnSetData gSource 'triggerAttack Nil) ) )
)

Language.Text:
(if (msnGetData gSource 'playerDestroyedFreighter)
	(cat "\"Either your poor marksmanship or deliberate malice destroyed the"
		" freighter entrusted to your care! You will never work for us again, captain.\""
		)
	(cat "\"Your timidity under fire and your lack of skill doomed the poor crew"
		" of your freighter and its cargo!\""
		)
	)

(block (
	(ship (objGetName (objGetObjByID (msnGetData gSource 'freighterID)) 0))
	)
	(random (list
		"Glad to have you with us, %name%"
		(cat ship " reporting in")
		"Nice to have you on our wing"
		(cat ship " on course")
		))
	)
