Events.OnCreate:
(block Nil

	(switch
		;	This mission only works in Eridani

		(not (eq (sysGetNode) 'SE))
			(msnDestroy gSource)

		;	Set up the mission

		(block Nil
			)
		)
	)

Language.Text:
(list
	(cat "\"I heard that the Penitents sent ships all the way to "
		(sysGetName (msnGetProperty gSource 'nodeID))
		". They must really hate the Sisters.\""
		)
	(cat "\"Those crazy Oracus worshipping Penitents were seen in "
		(sysGetName (msnGetProperty gSource 'nodeID))
		". They just jumped in and started attacking everyone!\""
		)
	)
