Events.GetGlobalRumors:
(block (
	(missionObj (@ (msnFind "* +unid:&msBenedictAndFiona;") 0))
	)
	(switch
		(not missionObj)
			Nil

		; Rumor can trigger up to 2 gates from source
		(gr (sysGetTopologyDistance (msnGetProperty missionObj 'nodeID) (sysGetNode)) 2)
			Nil

		(msnGetProperty missionObj 'isCompleted)
			(msfSimpleRumors (typTranslate  &rumorBenedictAndFiona; 'Rumors {
				sysName: (sysGetName (msnGetProperty missionObj 'nodeID))}
				))

		Nil
		)
	)

Language.Text:
(list
	(cat "\"I heard that the Penitents sent ships all the way to "
		(@ gData sysName) ". They must really hate the Sisters.\""
		)
	(cat "\"Those crazy Oracus worshipping Penitents were seen in "
		(@ gData sysName) ". They just jumped in and started attacking everyone!\""
		)
	)
